import React from 'react';
import './homepage.css'
import '@progress/kendo-theme-default/dist/all.css';
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

const items=['My Profile', 'Friend Requests', 'Account Settings', 'Support', 'Log Out'];


export default class HowItWorks extends React.Component {
    state = {
        designer: true,
        manufacturer: false,
        buyer:false,
        dropdownOpen: false
    }

    toggle = ()  => {
        this.setState({
          dropdownOpen: !this.state.dropdownOpen
        });
      }

    DropDownButton = () => {
        return <div>
        <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
          <DropdownToggle caret>
            Button Dropdown
          </DropdownToggle>
          <DropdownMenu>
            <DropdownItem header>Header</DropdownItem>
            <DropdownItem disabled>Action</DropdownItem>
            <DropdownItem>Another Action</DropdownItem>
            <DropdownItem divider />
            <DropdownItem>Another Action</DropdownItem>
          </DropdownMenu>
        </ButtonDropdown>
      </div>
    }

    render() {
      return (
          <div>
              <div className="how-it-works" style={{height:'800px'}}>
                  <div className="container">
                    <h1 className="howitworks-title">{this.DropDownButton}</h1>
                    <p className="howitworks-small-title"> I am a </p>
                  </div>
              </div>
            </div>
      );
    }
  }
